import React, { useState, useEffect } from "react";

function App() {
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState(""); // âœ… Success/Error message

  // âœ… Fetch data from Java Servlet
  const getMessageFromJava = async () => {
    const res = await fetch("http://localhost:9090/maven-archetype-webapp/hello");
    const data = await res.text();
    setMessage(data);
  };

  // âœ… Fetch users from Node.js + MySQL
  const fetchUsers = async () => {
    const res = await fetch("http://localhost:3000/users");
    const data = await res.json();
    setUsers(data);
  };

  // âœ… Add new user to database
  const addUser = async () => {
    if (!name || !email) {
      setStatus("âš  Please fill both fields!");
      return;
    }

    const res = await fetch("http://localhost:3000/add-user", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    });

    if (res.ok) {
      setStatus("âœ… User added successfully!");
      setName(""); // Clear input
      setEmail("");
      fetchUsers(); // âœ… Refresh list
    } else {
      setStatus("âŒ Failed to add user!");
    }
  };

  useEffect(() => {
    getMessageFromJava();
    fetchUsers();
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h2>ðŸš€ React + Node + Java Integration</h2>

      <p>âœ… <b>Data from Java Backend:</b><br />{message}</p>

      <p>âœ… <b>Users from Node.js + MySQL:</b></p>
      {users.length > 0 ? (
        users.map((user, index) => (
          <p key={index}>{user.name} - {user.email}</p>
        ))
      ) : (
        <p>No users or still loading...</p>
      )}

      <h3>âž• Add New User</h3>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <br /><br />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <br /><br />
      <button onClick={addUser}>Add User</button>

      {/* âœ… Success/Error message */}
      {status && <p>{status}</p>}
    </div>
  );
}

export default App;
