import React, { useEffect, useState } from "react";
import { getMessageFromJava, getUsersFromNode, addUserToNode } from "./services/api";

function App() {
  const [message, setMessage] = useState("Loading message from Java Servlet...");
  const [users, setUsers] = useState([]); // âœ… FIXED
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    getMessageFromJava().then((res) => setMessage(res));
    getUsersFromNode().then((res) => setUsers(res));
  }, []);

  const handleAddUser = () => {
    if (!name || !email) return;
    addUserToNode({ name, email }).then(() => {
      getUsersFromNode().then((res) => setUsers(res));
      setName("");
      setEmail("");
    });
  };

  return (
    <div>
      <h1>ðŸš€ React + Node + Java Integration</h1>

      <h2>âœ… Data from Java Backend:</h2>
      <p>{message}</p>

      <h2>âœ… Users from Node.js + MySQL:</h2>
      {users.length > 0 ? (
        users.map((user, i) => <p key={i}>{user.name} - {user.email}</p>)
      ) : (
        <p>No users or still loading...</p>
      )}

      <h2>âž• Add New User</h2>
      <input type="text" value={name} placeholder="Enter Name" onChange={(e) => setName(e.target.value)} />
      <input type="email" value={email} placeholder="Enter Email" onChange={(e) => setEmail(e.target.value)} style={{ marginLeft: "10px" }} />
      <button onClick={handleAddUser} style={{ marginLeft: "10px" }}>Add User</button>
    </div>
  );
}

export default App;
