import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ name: '', email: '' });
  const [editingUser, setEditingUser] = useState(null);

  // Fetch data from Java Servlet
  useEffect(() => {
    axios.get('http://localhost:9090/maven-archetype-webapp/hello')
      .then(res => setMessage(res.data))
      .catch(err => console.error('Error fetching from Java:', err));
  }, []);

  // Fetch users from Node backend
  const fetchUsers = () => {
    axios.get('http://localhost:3000/users')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Error fetching users:', err));
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Add user
  const handleAddUser = () => {
    if (!newUser.name || !newUser.email) return alert('Enter name and email!');
    axios.post('http://localhost:3000/users', newUser)
      .then(() => {
        setNewUser({ name: '', email: '' });
        fetchUsers();
      })
      .catch(err => console.error('Error adding user:', err));
  };

  // Edit user
  const handleEditClick = (user) => {
    setEditingUser(user);
  };

  // Update user
  const handleUpdateUser = () => {
    if (!editingUser.name || !editingUser.email) return alert('Enter valid details!');
    axios.put(`http://localhost:3000/users/${editingUser.id}`, editingUser)
      .then(() => {
        setEditingUser(null);
        fetchUsers();
      })
      .catch(err => console.error('Error updating user:', err));
  };

  return (
    <div style={{ fontFamily: 'Arial', padding: '20px' }}>
      <h2>ðŸš€ React + Node + Java Integration</h2>

      <h3>âœ… Data from Java Backend:</h3>
      <p>{message || 'Loading message from Java Servlet...'}</p>

      <h3>âœ… Users from Node.js + MySQL:</h3>
      {users.length === 0 ? (
        <p>No users or still loading...</p>
      ) : (
        <ul>
          {users.map((user) => (
            <li key={user.id}>
              {editingUser && editingUser.id === user.id ? (
                <>
                  <input
                    type="text"
                    value={editingUser.name}
                    onChange={(e) => setEditingUser({ ...editingUser, name: e.target.value })}
                  />
                  <input
                    type="email"
                    value={editingUser.email}
                    onChange={(e) => setEditingUser({ ...editingUser, email: e.target.value })}
                  />
                  <button onClick={handleUpdateUser}>Save</button>
                  <button onClick={() => setEditingUser(null)}>Cancel</button>
                </>
              ) : (
                <>
                  {user.name} - {user.email}
                  <button onClick={() => handleEditClick(user)} style={{ marginLeft: '10px' }}>Edit</button>
                </>
              )}
            </li>
          ))}
        </ul>
      )}

      <h3>âž• Add New User</h3>
      <input
        type="text"
        placeholder="Name"
        value={newUser.name}
        onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
      />
      <input
        type="email"
        placeholder="Email"
        value={newUser.email}
        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
      />
      <button onClick={handleAddUser}>Add User</button>
    </div>
  );
}

export default App;
