import React, { useEffect, useState } from "react";
import { getUsers, getMessageFromJava } from "./api";

function App() {
  const [users, setUsers] = useState([]);
  const [javaMessage, setJavaMessage] = useState("");

  // âœ… Fetch users from Node backend
  useEffect(() => {
    getUsers().then((data) => {
      if (data) setUsers(data);
    });

    // âœ… Fetch message from Java Servlet
    getMessageFromJava().then((message) => {
      if (message) setJavaMessage(message);
    });
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸš€ React + Node + Java Integration</h2>

      <h3>âœ… Data from Java Backend:</h3>
      <p>{javaMessage || "Loading message from Java Servlet..."}</p>

      <h3>âœ… Users from Node.js + MySQL:</h3>
      {users.length > 0 ? (
        <ul>
          {users.map((user, index) => (
            <li key={index}>
              {user.name} - {user.email}
            </li>
          ))}
        </ul>
      ) : (
        <p>No users or still loading...</p>
      )}
    </div>
  );
}

export default App;
