import React, { useEffect, useState } from "react";
import { getMessageFromJava, getUsersFromNode, addUserToNode } from "./services/api";

function App() {
  const [message, setMessage] = useState("Loading message from Java Servlet...");
  const [users, setUsers] = useState([]); // âœ… Fix: declare setUsers properly
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    // Fetch data from Java backend
    getMessageFromJava().then((res) => setMessage(res));

    // Fetch data from Node.js backend
    getUsersFromNode().then((res) => setUsers(res));
  }, []);

  const handleAddUser = () => {
    if (!name || !email) return;

    addUserToNode({ name, email }).then(() => {
      // Refresh the users list after adding user
      getUsersFromNode().then((res) => setUsers(res));
      setName("");
      setEmail("");
    });
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>ðŸš€ React + Node + Java Integration</h1>

      <h2>âœ… Data from Java Backend:</h2>
      <p>{message}</p>

      <h2>âœ… Users from Node.js + MySQL:</h2>
      {users.length > 0 ? (
        users.map((user, index) => (
          <p key={index}>{user.name} - {user.email}</p>
        ))
      ) : (
        <p>No users or still loading...</p>
      )}

      <h2>âž• Add New User</h2>
      <input
        type="text"
        placeholder="Enter Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Enter Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ marginLeft: "10px" }}
      />
      <button onClick={handleAddUser} style={{ marginLeft: "10px" }}>
        Add User
      </button>
    </div>
  );
}

export default App;
